CREATE VIEW qryRepC2 AS  Select qryRepCV2.PKProducto,qryRepCV2.Codigo, qryRepCV2.Descripcion, qryRepCV2.Unidad, qryRepCV2.ND_Fecha, qryRepCV2.IAgente, qryRepCV2.ICConsumo, qryRepCV2.ICliente, qryRepCV2.Tipo, Sum(qryRepCV2.Ventas) As Ventas, Sum(qryRepCV2.Importe) as Importe,Sum(qryRepCV2.Impuestos) as ImpuestosVenta, Sum(qryRepCV2.Costo) As Costo,0 as Devoluviones,0 as ImpDev,0 as  ImpuestosDev,0 as CostoDev From qryRepCV2 group by qryRepCV2.PKProducto,qryRepCV2.Codigo, qryRepCV2.Descripcion, qryRepCV2.Unidad, qryRepCV2.ND_Fecha, qryRepCV2.IAgente, qryRepCV2.ICConsumo, qryRepCV2.ICliente, qryRepCV2.Tipo  UNION ALL Select qryRepCD2.PKProducto,qryRepCD2.Codigo, qryRepCD2.Descripcion, qryRepCD2.Unidad,qryRepCD2.ND_Fecha, qryRepCD2.ICConsumo, qryRepCD2.IAgente, qryRepCD2.ICliente, qryRepCD2.Tipo, 0 As Ventas,0 As Importe,0 as ImpuestosVenta, 0 As Costo, Sum(qryRepCD2.Ventas) As Devoluciones, Sum(qryRepCD2.Importe) as ImpDev, Sum(qryRepCD2.Impuestos) as ImpuestosDev, Sum(qryRepCD2.Costo) As CostoDev From qryRepCD2 group by qryRepCD2.PKProducto,qryRepCD2.Codigo, qryRepCD2.Descripcion, qryRepCD2.Unidad, qryRepCD2.ND_Fecha, qryRepCD2.IAgente, qryRepCD2.ICConsumo, qryRepCD2.ICliente, qryRepCD2.Tipo;

CREATE VIEW qryRepCostoVentas2 AS  SELECT TOP 100 PERCENT qryrepc2.PKProducto, qryrepc2.Codigo, qryrepc2.Descripcion, qryrepc2.Unidad, qryrepc2.ND_Fecha, qryrepc2.IAgente, qryrepc2.ICConsumo, qryrepc2.ICliente, qryrepc2.Tipo, sum(qryrepc2.Ventas) AS Ventas, sum(qryrepc2.Importe) AS Importe, sum(qryrepc2.ImpuestosVenta) AS ImpuestosV, Sum(qryrepc2.Costo) AS Costo, Sum(qryrepc2.Devoluviones) AS Devoluciones, Sum(qryrepc2.ImpDev) AS ImpDev, sum(qryrepc2.ImpuestosDev) AS DevImpuestos, Sum(qryrepc2.CostoDev) AS CtDev FROM qryrepc2 GROUP BY qryrepc2.PKProducto, qryrepc2.Codigo, qryrepc2.Descripcion, qryrepc2.Unidad, qryrepc2.ND_Fecha, qryrepc2.IAgente, qryrepc2.ICConsumo, qryrepc2.ICliente, qryrepc2.Tipo ORDER BY qryrepc2.Descripcion;

