CREATE VIEW qryRequisicionesASurtir AS select TOP 100 PERCENT docf_Documento.sys_pk AS PKDocumento, linea.Descripcion as ldescripcion,docf_Documento.sys_dtcreated, docf_Documento.IAlmacen, docf_Documento.IAlmacen_Destino, Producto.Codigo, Producto.Descripcion as pdescripcion, docf_DDocumento.Unidad,  Sum(docf_DDocumento.Cantidad) AS Cantidad, BlockDocumentos.Serie, FoliosDocumentos.Folio,isnull(Existencias.Existencia,0) AS Existencia, docf_Status.Descripcion AS Status from ((((docf_Documento inner join (docf_DDocumento inner join Producto on docf_DDocumento.IProducto=Producto.Sys_PK) on docf_Documento.sys_pk=docf_DDocumento.IDocumento) inner join Linea on Producto.ILinea=Linea.Sys_PK)INNER JOIN (FoliosDocumentos INNER JOIN BlockDocumentos on FoliosDocumentos.Block=BlockDocumentos.sys_pk) on docf_documento.IFolio=FoliosDocumentos.sys_pk) Left Join Existencias ON  docf_Documento.IAlmacen_Destino=Existencias.IAlmacen and docf_DDocumento.IProducto=Existencias.FK_Producto_IExistencias) LEFT JOIN docf_Status on docf_Documento.IStatus = docf_Status.Sys_PK Group by docf_Documento.sys_pk,linea.Descripcion ,docf_Documento.sys_dtcreated, docf_Documento.IAlmacen, docf_Documento.IAlmacen_Destino, Producto.Codigo, Producto.Descripcion , docf_DDocumento.Unidad,  BlockDocumentos.Serie, FoliosDocumentos.Folio,isnull(Existencias.Existencia,0), docf_Status.Descripcion ORDER BY linea.Descripcion, Producto.Descripcion
create view qryNvaRequisicionDeIngredientesAPartirDeRecetas as SELECT P2.Sys_PK, P2.IClase, dbo.docf_Documento.sys_pk AS IDocumento, dbo.docf_DDocumento.Cantidad * dbo.docf_DDocumento.Factor * dbo.IEnsamble.Cantidad AS cantidad, P2.Unidad, P2.FactorB, P2.CostoUltimo, P2.Precio1, P2.Existencia AS existenciaGlobal, dbo.docf_Documento.IAlmacen_Destino AS almacen FROM dbo.docf_DDocumento INNER JOIN dbo.docf_Documento LEFT OUTER JOIN dbo.Existencias ON dbo.docf_Documento.IAlmacen_Destino = dbo.Existencias.IAlmacen ON dbo.docf_DDocumento.IDocumento = dbo.docf_Documento.sys_pk INNER JOIN dbo.Producto INNER JOIN dbo.IEnsamble INNER JOIN dbo.Producto AS P2 ON dbo.IEnsamble.Elemento = P2.Sys_PK ON dbo.Producto.Sys_PK = dbo.IEnsamble.FK_Producto_Elementos ON dbo.docf_DDocumento.IProducto = dbo.Producto.Sys_PK WHERE dbo.Producto.IClase = 2 or producto.iclase = 5 GROUP BY P2.Sys_PK, P2.IClase, dbo.docf_Documento.sys_pk, dbo.docf_DDocumento.Cantidad, dbo.docf_DDocumento.Factor, dbo.IEnsamble.Cantidad, P2.Unidad, P2.FactorB, P2.CostoUltimo, P2.Precio1, P2.Existencia, dbo.docf_Documento.IAlmacen_Destino
