create view qryrepSaldoClientesX as select cliente.Sys_PK AS PKCliente,cliente.Codigo AS CCodigo,cliente.Nombre AS CNombre,dcxc.Referencia AS Referencia,dcxc.Aplicacion AS vencimiento,(DATEDIFF(DAY,GETDATE(),dcxc.Aplicacion)) as Dias,isnull(agente.Sys_PK,0) AS PKAgente,isnull(agente.Codigo,'0000') AS ACodigo,isnull(agente.Nombre,'Sin vendedor') AS ANombre,(((year(dcxc.Aplicacion) * 10000) + (month(dcxc.Aplicacion) * 100)) + day(dcxc.Aplicacion)) AS NDFecha,dcxc.Debe AS importe,(((dcxc.Debe + dcxc.IntMoratorios) - dcxc.Bonificaciones) - dcxc.Pagos) AS Saldo from (((dcxc join cliente on((dcxc.ICliente = cliente.Sys_PK))) left join venta on((dcxc.IVenta = venta.Sys_PK))) left join agente on((venta.IAgente = agente.Sys_PK))) where ((dcxc.Aplicable <> 0) and ((((dcxc.Debe + dcxc.IntMoratorios) - dcxc.Bonificaciones) - dcxc.Pagos) <> 0)) group by cliente.Sys_PK,cliente.Codigo,cliente.Nombre,dcxc.Referencia,dcxc.Aplicacion,dcxc.Debe,agente.Sys_PK,agente.Codigo,agente.Nombre,dcxc.IntMoratorios,dcxc.Aplicacion,dcxc.Pagos,dcxc.Bonificaciones,dcxc.Aplicacion;
create view qryAtenciones as SELECT ut_crmnotacaso.*, ut_crmcasos.fk_tipocaso, day(ut_crmnotacaso.Sys_DTCreated) as Dia, DATEPART(wk, ut_crmnotacaso.Sys_DTCreated) AS semana, Month(ut_crmnotacaso.Sys_DTCreated) as Mes, YEAR(ut_crmnotacaso.Sys_DTCreated) as Anio, ((year(ut_crmnotacaso.Sys_DTCreated) * 10000) + (month(ut_crmnotacaso.Sys_DTCreated) * 100) + (day(ut_crmnotacaso.Sys_DTCreated))) as NDCreacion, Cliente.Sys_PK as PKCliente, Cliente.Codigo as CCliente, Cliente.Nombre as Cliente FROM ut_crmnotacaso inner join ut_crmcasos on ut_crmnotacaso.fk_caso = ut_crmcasos.sys_pk inner join cliente on ut_crmcasos.fk_cliente = cliente.sys_pk;
CREATE VIEW qryadmoncasos AS SELECT DISTINCT TOP (100) PERCENT Cliente.Sys_PK AS PKCliente, Cliente.Codigo AS CCliente, Cliente.Nombre AS Cliente, ut_crmcasos.asunto, ut_crmtipocaso.Sys_PK AS PKTipoCaso, ut_crmtipocaso.uf_descripcion AS TipoCaso, ut_crmprioridad.descripcion AS Prioridad,  ut_crmcasos.Sys_PK AS PKCaso, ut_crmcasos.usuarioabre, ut_crmcasos.usuarioasignado, ut_crmcasos.usuariocierra,  ut_crmcasos.fechavencimiento AS vencimiento, case when DATEDIFF(ss, ut_crmcasos.fechavencimiento, GETDATE()) < 0 then CONVERT(varchar(8), dateadd(s, case when DATEDIFF(ss, ut_crmcasos.fechavencimiento, GETDATE()) < 0 then DATEDIFF(ss, ut_crmcasos.fechavencimiento, GETDATE()) * - 1 else DATEDIFF(ss, ut_crmcasos.fechavencimiento, GETDATE()) end, 0), 108) else '-' + (CONVERT(varchar(8), dateadd(s, case when DATEDIFF(ss, ut_crmcasos.fechavencimiento, GETDATE()) < 0 then DATEDIFF(ss, ut_crmcasos.fechavencimiento, GETDATE()) * - 1 else DATEDIFF(ss, ut_crmcasos.fechavencimiento, GETDATE()) end, 0), 108)) end AS mDiferencia, ut_crmcasos.fechaapertura, ut_crmcasos.horaapertura, ut_crmcasos.fechacierre, ut_crmcasos.horacierre, CONVERT(datetime, CONVERT(VARCHAR(10), dbo.ut_crmcasos.fechacierre, 120) + ' ' + CONVERT(VARCHAR(10), ut_crmcasos.horacierre, 108), 120) AS cierre, ISNULL(ut_crmcasos.status, 0) AS status, ut_crmcasos.Sys_DTCreated AS creado, ut_crmcasos.Sys_TimeStamp AS ActualizacionCaso, ISNULL((SELECT MAX(Sys_TimeStamp) AS Actualizacion FROM ut_crmnotacaso WHERE (fk_caso = ut_crmcasos.Sys_PK)), ut_crmcasos.Sys_TimeStamp) AS UltimaActualizacion, ut_crmcasos.uf_color FROM ut_crmcasos INNER JOIN Cliente ON ut_crmcasos.fk_cliente = Cliente.Sys_PK INNER JOIN ut_crmtipocaso ON ut_crmcasos.fk_tipocaso = ut_crmtipocaso.Sys_PK LEFT OUTER JOIN ut_crmprioridad ON ut_crmcasos.fk_prioridad = ut_crmprioridad.Sys_PK ORDER BY PKCaso DESC;
CREATE VIEW qryadmoncasos_2 as select z.PKCaso AS PKCaso,z.Prioridad AS Prioridad,z.TipoCaso AS TipoCaso,z.PKTipoCaso AS PKTipoCaso,z.asunto AS asunto,z.usuarioabre AS usuarioabre, isnull(z.usuarioasignado,'') AS usuarioasignado,z.usuariocierra AS usuariocierra,z.status AS status, z.vencimiento AS vencimiento, z.PKCliente, z.CCliente AS ccliente,z.Cliente AS cliente, convert(VARCHAR(19), CASE WHEN z.ActualizacionCaso >= z.UltimaActualizacion THEN z.ActualizacionCaso ELSE z.UltimaActualizacion END, 120) AS Actualizacion, z.uf_color AS uf_color, case when (z.mDiferencia like '-%' or (z.cierre >= z.vencimiento)) then '-' + CONVERT(VARCHAR(19), CASE WHEN z.status = 1 THEN (case when (z.cierre >= z.vencimiento) then z.cierre - z.vencimiento else z.vencimiento - z.cierre end)	ELSE convert(datetime, replace(z.mDiferencia, '-','')) END, 108) else CONVERT(VARCHAR(19), CASE WHEN z.status = 1 THEN (case when (z.cierre >= z.vencimiento) then z.cierre - z.vencimiento else z.vencimiento - z.cierre end) ELSE convert(datetime, replace(z.mDiferencia, '-','')) END, 108) end AS mDiferencia, (((year(z.creado) * 10000) + (month(z.creado) * 100)) + DATEPART(DD, z.creado)) AS NDCreacion, datepart(wk, z.vencimiento) AS semana, datepart(wk, z.fechaapertura) AS semanaapertura, datepart(wk, z.fechacierre) AS semanacierre, z.fechaapertura, z.horaapertura, z.fechacierre, z.horacierre, (((year(z.fechacierre) * 10000) + (month(z.fechacierre) * 100)) + datepart(dd, z.fechacierre)) AS NDCierre from qryadmoncasos z;
