CREATE VIEW qryPoliza AS SELECT TOP 100 PERCENT cpoliza.Sys_PK AS Sys_PK, foliospolizas.fet, ctipospolizas.Const AS Tipo, cpoliza.Fecha AS Fecha, cpoliza.Concepto AS Concepto, cpoliza.Debe AS Debe, cpoliza.Haber AS Haber, cpoliza.Aplicada AS Aplicada, cpoliza.Referencia AS Referencia, cpoliza.Auditada AS Auditada, cpoliza.Notas AS Notas, foliospolizas.fe, foliospolizas.fp, foliospolizas.fpt FROM (ctipospolizas INNER JOIN cpoliza ON (ctipospolizas.ID = cpoliza.Tipo)) LEFT JOIN foliospolizas ON cpoliza.sys_pk = foliospolizas.fk_poliza ORDER BY ctipospolizas.Const, foliospolizas.fet, cpoliza.Fecha;
