create view qryCRMVentas as select Venta.Sys_PK as PKVenta, Venta.Fecha, ((DVenta.Cantidad * DVenta.Factor) - ifnull(z.CantidadE,0)) as Cantidad, ((DVenta.Impuesto1 + DVenta.Impuesto2 + DVenta.Impuesto3 + DVenta.Impuesto4 + (DVenta.Cantidad * DVenta.Factor * DVenta.Precio) - DVenta.Descuento1 - DVenta.Descuento2))  - ifnull(((z.precio * z.cantidadE * z.TipoCambio) + z.impuesto1 + z.impuesto2 + z.impuesto3 + z.impuesto4 - z.Descuento1 - z.Descuento2),0) as Importe, Venta.Referencia, DATE_FORMAT(Venta.Fecha, '%d/%m/%Y') as SFecha, Producto.Sys_PK, Producto.Codigo, Producto.Descripcion, year(Venta.Fecha) as Anio, month(Venta.Fecha) as mes, Venta.ICliente from DVenta inner join Venta on Dventa.FK_Venta_Detalle = Venta.Sys_PK inner join Producto on DVenta.IProducto = Producto.Sys_PK left join qrydevventasproductos z on Venta.Sys_PK = z.PKVenta and DVenta.Sys_PK = z.PKDVenta and Producto.Sys_PK = z.PKProducto Where (Venta.Documento = 3) AND (Venta.StatusFacturacion = 1) AND (Venta.StatusAdministrativo <> 99) OR (Venta.Documento = 6) AND (Venta.StatusFacturacion = 1) AND (Venta.StatusAdministrativo <> 99) OR (Venta.Documento = 4) AND (Venta.StatusAdministrativo <> 99) ORDER BY Venta.Fecha desc;
create view qrycrmsaldoclientes AS select cliente.Sys_PK AS PKCliente,cliente.Codigo AS CCodigo,cliente.Nombre AS CNombre,dcxc.Referencia AS Referencia,dcxc.Aplicacion AS vencimiento,(to_days(now()) - to_days(dcxc.Aplicacion)) AS dias,ifnull(agente.Sys_PK,0) AS PKAgente,ifnull(agente.Codigo,_latin1'0000') AS ACodigo,ifnull(agente.Nombre,_latin1'Sin vendedor') AS ANombre,(((year(dcxc.Aplicacion) * 10000) + (month(dcxc.Aplicacion) * 100)) + day(dcxc.Aplicacion)) AS NDFecha,dcxc.Debe AS importe,(((dcxc.Debe + dcxc.IntMoratorios) - dcxc.Bonificaciones) - dcxc.Pagos) AS Saldo from (((dcxc join cliente on((dcxc.ICliente = cliente.Sys_PK))) left join venta on((dcxc.IVenta = venta.Sys_PK))) left join agente on((venta.IAgente = agente.Sys_PK))) where ((dcxc.Aplicable <> 0) and ((((dcxc.Debe + dcxc.IntMoratorios) - dcxc.Bonificaciones) - dcxc.Pagos) <> 0)) group by cliente.Sys_PK,cliente.Codigo,cliente.Nombre,dcxc.Referencia,dcxc.Aplicacion,dcxc.Debe;
create view qryCRMAntiguedadSaldos as SELECT DCxC.Sys_PK PKDCXC, Z.Sys_PK as PKCliente, DATE_FORMAT(DCxC.Fecha, '%d/%m/%Y') as Fecha, DATE_FORMAT(DCxC.Aplicacion, '%d/%m/%Y') as Aplicacion, DCxC.Referencia, case when (to_days(now()) - to_days(dcxc.Aplicacion)) <= 0 then (DCxC.Debe+DCxC.IntMoratorios-DCxC.Bonificaciones-DCxC.Pagos) else 0 end as No_Vencido, case when ((to_days(now()) - to_days(dcxc.Aplicacion)) >= 1 AND (to_days(now()) - to_days(dcxc.Aplicacion)) <= 30) then (DCxC.Debe+DCxC.IntMoratorios-DCxC.Bonificaciones-DCxC.Pagos) else 0 end as menor30, case when ((to_days(now()) - to_days(dcxc.Aplicacion)) >= 31 AND (to_days(now()) - to_days(dcxc.Aplicacion)) <= 60) then (DCxC.Debe+DCxC.IntMoratorios-DCxC.Bonificaciones-DCxC.Pagos) else 0 end as menor60, case when ((to_days(now()) - to_days(dcxc.Aplicacion)) >= 61 AND (to_days(now()) - to_days(dcxc.Aplicacion)) <= 90) then (DCxC.Debe+DCxC.IntMoratorios-DCxC.Bonificaciones-DCxC.Pagos) else 0 end as menor90, case when (to_days(now()) - to_days(dcxc.Aplicacion)) >= 91 then (DCxC.Debe+DCxC.IntMoratorios-DCxC.Bonificaciones-DCxC.Pagos) else 0 end as mayor90 FROM (Cliente Z INNER JOIN (cDocumentos INNER JOIN DCxC ON cDocumentos.ID = DCxC.Documento) ON Z.Sys_PK = DCxC.ICliente) WHERE DCxC.Aplicable<>0 AND (DCxC.Debe+DCxC.IntMoratorios-DCxC.Bonificaciones-DCxC.Pagos)<>0 ORDER BY dcxc.Sys_PK;
