CREATE VIEW qryRequisicionesASurtir AS select docf_Documento.sys_pk AS PKDocumento, linea.Descripcion as ldescripcion,docf_Documento.sys_dtcreated, docf_Documento.IAlmacen, docf_Documento.IAlmacen_Destino, Producto.Codigo, Producto.Descripcion as pdescripcion, docf_DDocumento.Unidad,  Sum(docf_DDocumento.Cantidad) AS Cantidad, BlockDocumentos.Serie, FoliosDocumentos.Folio,if(isnull(Existencias.Existencia),0,Existencias.Existencia) AS Existencia, docf_Status.Descripcion AS Status from ((((docf_Documento inner join (docf_DDocumento inner join Producto on docf_DDocumento.IProducto=Producto.Sys_PK) on docf_Documento.sys_pk=docf_DDocumento.IDocumento) inner join Linea on Producto.ILinea=Linea.Sys_PK)INNER JOIN (FoliosDocumentos INNER JOIN BlockDocumentos on FoliosDocumentos.Block=BlockDocumentos.sys_pk) on docf_documento.IFolio=FoliosDocumentos.sys_pk) Left Join Existencias ON  docf_Documento.IAlmacen_Destino=Existencias.IAlmacen and docf_DDocumento.IProducto=Existencias.FK_Producto_IExistencias) LEFT JOIN docf_Status on docf_Documento.IStatus = docf_Status.Sys_PK Group by docf_Documento.sys_pk,linea.Descripcion ,docf_Documento.sys_dtcreated, docf_Documento.IAlmacen, docf_Documento.IAlmacen_Destino, Producto.Codigo, Producto.Descripcion , docf_DDocumento.Unidad,  BlockDocumentos.Serie, FoliosDocumentos.Folio,if(isnull(Existencias.Existencia),0,Existencias.Existencia), docf_Status.Descripcion ORDER BY linea.Descripcion, Producto.Descripcion;
create view qryNvaRequisicionDeIngredientesAPartirDeRecetas as SELECT  P2.Sys_PK, P2.IClase, docf_Documento.sys_pk AS IDocumento, docf_DDocumento.Cantidad * docf_DDocumento.Factor * IEnsamble.Cantidad AS cantidad, P2.Unidad, P2.FactorB, P2.CostoUltimo, P2.Precio1, P2.Existencia AS existenciaGlobal, docf_Documento.IAlmacen_Destino AS almacen from (docf_DDocumento inner join (docf_documento left join existencias on docf_documento.ialmacen_destino=existencias.ialmacen) on docf_ddocumento.idocumento=docf_documento.sys_pk) inner join (producto inner join (iensamble inner join producto p2 on iensamble.elemento=p2.sys_pk) on producto.sys_pk=iensamble.fk_producto_elementos) on docf_ddocumento.iproducto=producto.sys_pk WHERE Producto.IClase = 2 or producto.iclase = 5 GROUP BY P2.Sys_PK, P2.IClase, docf_Documento.sys_pk, docf_DDocumento.Cantidad, docf_DDocumento.Factor, IEnsamble.Cantidad, P2.Unidad, P2.FactorB, P2.CostoUltimo, P2.Precio1, P2.Existencia, docf_Documento.IAlmacen_Destino;
create view qRequisiciones as SELECT docf_Documento.Sys_PK, DATE_FORMAT(docf_Documento.sys_dtcreated, '%d/%m/%Y') as Fecha, docf_tipo.Descripcion as Tipo_Requisicion, docf_Status.ID as IDStatus, docf_Status.Descripcion as Status, BlockDocumentos.Serie, FoliosDocumentos.Folio, TUser.UserName as Remitente, t2.UserName as Destinatario, docf_Documento.Asunto, Almacen.Descripcion as almaceno, a2.Descripcion as almacend, YEAR(docf_documento.sys_dtcreated) as Anio, month(docf_documento.sys_dtcreated) as mes, docf_Tipo.ID as IDTipo, ifnull(docf_documento.sys_deleted,0) as Eliminado, TUser.Sys_PK as PKRemitente, t2.Sys_PK as PKDestinatario, (docf_ddocumento.factor * docf_ddocumento.cantidad) as CantidadReal, docf_ddocumento.ejercido as Ejercido, docf_Documento.uf_Color FROM (((((((docf_Documento INNER JOIN docf_Status on docf_Documento.IStatus = docf_Status.sys_pk) INNER JOIN FoliosDocumentos on docf_Documento.IFolio = FoliosDocumentos.Sys_PK) INNER JOIN BlockDocumentos on FoliosDocumentos.Block = BlockDocumentos.Sys_PK) LEFT JOIN TUser on docf_documento.IUsuario = TUser.Sys_PK) LEFT JOIN TUser t2 on docf_Documento.IDestinatario = t2.Sys_PK) LEFT JOIN Almacen on (docf_Documento.IAlmacen = Almacen.Sys_PK) LEFT JOIN Almacen a2 on docf_Documento.IAlmacen_Destino = a2.Sys_PK) LEFT JOIN docf_Tipo on docf_Documento.ITipo = docf_Tipo.sys_pk) LEFT JOIN docf_DDocumento on docf_Documento.sys_pk = docf_DDocumento.IDocumento ORDER BY SYS_PK;
