create view qryrepSaldoClientesX as select cliente.Sys_PK AS PKCliente,cliente.Codigo AS CCodigo,cliente.Nombre AS CNombre,dcxc.Referencia AS Referencia,dcxc.Aplicacion AS vencimiento,(to_days(now()) - to_days(dcxc.Aplicacion)) AS dias,ifnull(agente.Sys_PK,0) AS PKAgente,ifnull(agente.Codigo,'0000') AS ACodigo,ifnull(agente.Nombre,'Sin vendedor') AS ANombre,(((year(dcxc.Aplicacion) * 10000) + (month(dcxc.Aplicacion) * 100)) + dayofmonth(dcxc.Aplicacion)) AS NDFecha,dcxc.Debe AS importe,(((dcxc.Debe + dcxc.IntMoratorios) - dcxc.Bonificaciones) - dcxc.Pagos) AS Saldo from (((dcxc join cliente on((dcxc.ICliente = cliente.Sys_PK))) left join venta on((dcxc.IVenta = venta.Sys_PK))) left join agente on((venta.IAgente = agente.Sys_PK))) where ((dcxc.Aplicable <> 0) and ((((dcxc.Debe + dcxc.IntMoratorios) - dcxc.Bonificaciones) - dcxc.Pagos) <> 0)) group by cliente.Sys_PK,cliente.Codigo,cliente.Nombre,dcxc.Referencia,dcxc.Aplicacion,dcxc.Debe;
