CREATE VIEW qryadmoncasos AS select distinct Cliente.Sys_PK as PKCliente, cliente.Codigo AS CCliente,cliente.Nombre AS Cliente,ut_crmcasos.asunto AS asunto,ut_crmtipocaso.Sys_PK AS PKTipoCaso,ut_crmtipocaso.uf_descripcion AS TipoCaso,ut_crmprioridad.descripcion AS Prioridad,ut_crmcasos.Sys_PK AS PKCaso,ut_crmcasos.usuarioabre AS usuarioabre,ut_crmcasos.usuarioasignado AS usuarioasignado,ut_crmcasos.usuariocierra AS usuariocierra,ut_crmcasos.fechavencimiento AS vencimiento,timediff(ut_crmcasos.fechavencimiento,now()) AS mDiferencia,ut_crmcasos.fechaapertura AS fechaapertura,ut_crmcasos.horaapertura AS horaapertura,ut_crmcasos.fechacierre AS fechacierre,ut_crmcasos.horacierre AS horacierre,date_format(concat(cast(ut_crmcasos.fechacierre as date),' ',cast(ut_crmcasos.horacierre as time)),'%Y-%m-%d %H:%i:%s') AS cierre,ifnull(ut_crmcasos.status,0) AS status,ut_crmcasos.Sys_DTCreated AS creado,ut_crmcasos.Sys_TimeStamp AS ActualizacionCaso,ifnull((select max(ut_crmnotacaso.Sys_TimeStamp) AS Actualizacion from ut_crmnotacaso where (ut_crmnotacaso.fk_caso = ut_crmcasos.Sys_PK)),ut_crmcasos.Sys_TimeStamp) AS UltimaActualizacion,ut_crmcasos.uf_color AS uf_color from (((ut_crmcasos join cliente on((ut_crmcasos.fk_cliente = cliente.Sys_PK))) join ut_crmtipocaso on((ut_crmcasos.fk_tipocaso = ut_crmtipocaso.Sys_PK))) left join ut_crmprioridad on((ut_crmcasos.fk_prioridad = ut_crmprioridad.Sys_PK))) order by ut_crmcasos.Sys_PK desc;
CREATE VIEW qryadmoncasos_2 AS select z.PKCaso AS PKCaso,z.Prioridad AS Prioridad,z.TipoCaso AS TipoCaso,z.PKTipoCaso AS PKTipoCaso,z.asunto AS asunto,z.usuarioabre AS usuarioabre, ifnull(z.usuarioasignado,'') AS usuarioasignado,z.usuariocierra AS usuariocierra,z.status AS status,z.vencimiento AS vencimiento, z.PKCliente, z.CCliente AS ccliente,z.Cliente AS cliente, DATE_FORMAT(if((z.ActualizacionCaso >= z.UltimaActualizacion),z.ActualizacionCaso,z.UltimaActualizacion), '%Y-%m-%d %H:%i:%s') AS Actualizacion,z.uf_color AS uf_color, cast(if(z.status = 1, timediff(z.vencimiento, if(z.status = 0, now(), z.cierre)), z.mDiferencia) as char) AS mDiferencia, (((year(z.creado) * 10000) + (month(z.creado) * 100)) + dayofmonth(z.creado)) AS NDCreacion,week(z.vencimiento,1) AS semana, week(z.fechaapertura,1) AS semanaapertura, week(z.fechacierre,1) AS semanacierre, z.fechaapertura, z.horaapertura, z.fechacierre, z.horacierre, (((year(z.fechacierre) * 10000) + (month(z.fechacierre) * 100)) + dayofmonth(z.fechacierre)) AS NDCierre from qryadmoncasos z;
